import React, { useState } from 'react'

const Form = () => {

    const [text, setText] = useState('')
    const [password, setPassword] = useState('akb')

    const handleChange = (e) => {
        setText(e.target.value)
    }

  return (
    <div className='border-4 m-8'>
        <div className='p-4 flex flex-col'>
            <p>Enter the desired length of password:</p>
            <input type='text' className='w-[5rem] border-4 m-4 mr-0 p-0' onChange={handleChange} value={text}/>
        </div>
        <button type='submit' className='border-4 w-[6rem] m-4'>Submit</button>
        <p>{password}</p>

    </div>
  )
}

export default Form
