import React, { useState } from 'react'

const Form = () => {

    const [length, setLength] = useState('')
    const [password, setPassword] = useState('')

    let isLength = false

    const handleChange = (e) => {
        setLength(e.target.value)
        if(e.target.value < 8 || e.target.value > 20){
            isLength = false
        }
    }

    const handlePassword = (e) => {
        e.preventDefault()
        let alphabet = 'qwertyuiopasdfghjklzxcvbnmQWERTYUIOPASDFGHJKLZXCVBNM'
        let num = '1234567890'
        let specialCharacter = '@!#$&*'
        let input = ''
        for(let i=0; i<length; i++){
            let generate = alphabet[Math.floor(Math.random() * 52)] + num[Math.floor(Math.random() * 10)] + specialCharacter[Math.floor(Math.random() * 6)]
            input += generate
        }
        setPassword(input.substr(0,length))
    }

  return (
    <div>
        <div className='border-4 m-8'>
            <form className='p-4 flex flex-col' onSubmit={handlePassword}>
                <p>Enter the desired length of password:</p>
                <p>{!isLength ? 'Password must be between 8-20 characters' : ''}</p>
                <input type='text' className='w-[5rem] border-4 m-4 mr-0 p-0' onChange={handleChange} value={length}/>
                <button type='submit' className='border-4 w-[6rem] m-4'>Submit</button> 
            </form>
        </div>
        <p className='text-3xl text-center'>{password}</p>
    </div>
  )
}

export default Form
